import React, { Component } from 'react';
import Aux from '../../hoc/Aux/Aux';
import Budget from '../../components/budget/Budget';
import BuildControl from './BuildControl/BuildControl';
import { updateObject, percentageCalculator } from '../../Utility/Utility';
import Modal from '../../components/UI/Modal/Modal';
import ExpSummary from '../../components/BudgetSummary/ExpSummary/ExpSummary';
import Button from '../../components/UI/Button/Button';


class BudgetBuilder extends Component {

    state = {
        budgetSummary: {
            inc: [],
            exp: []
        },
        totalBudget: 0,
        totalIncome: 0,
        totalExpenses: 0,
        totalPercentage: null,
        showSummary: false
    }

    addSummaryHandler = data => {
        let id;
        if (this.state.budgetSummary[data.type].length === 0) {
            id = 0;
        } else {
            id = this.state.budgetSummary[data.type][this.state.budgetSummary[data.type].length - 1].id + 1;
        }
        let summary = {
                    des: data.description, 
                    val: parseFloat(data.value).toFixed(2),
                    id: id
                };

        if (data.type === 'exp') {
            summary = {
                des: data.description, 
                val: parseFloat(data.value).toFixed(2),
                id: id ,
                perc: percentageCalculator(data.value, this.state.totalIncome)
            }
        }

        const updatedIncExp = [ 
            ...this.state.budgetSummary[data.type] 
            ];

        updatedIncExp.push(summary);

        const updatedBudgetSummary = updateObject(this.state.budgetSummary,{
            [data.type]: updatedIncExp
        });

        let budget = this.state.totalBudget;
        let income = this.state.totalIncome;
        let expense = this.state.totalExpenses;

        if (data.type === 'inc') {
            budget = budget + parseFloat(data.value);
            income = income + parseFloat(data.value);
        }

        if (data.type === 'exp') {
            budget = budget - parseFloat(data.value);
            expense = expense + parseFloat(data.value);
        }

        this.setState({
            budgetSummary: updatedBudgetSummary,
            totalBudget: budget,
            totalIncome: income,
            totalExpenses: expense
        });

    }

    showSummaryHandler = () => {
        this.setState({showSummary: true});
    }

    hideSummaryHandler = () => {
        this.setState({showSummary: false});
    }

    componentDidUpdate() {
        console.log(this.state);
    }

    render () {
        return (
            <Aux>
                <Modal show={this.state.showSummary} modalClosed={this.hideSummaryHandler}>
                    <ExpSummary 
                    expensesSummary={this.state.budgetSummary.exp} 
                    delete={this.deleteItemHandler} />
                </Modal> 
                <Budget
                totalBudget={this.state.totalBudget}
                totalIncome={this.state.totalIncome}
                totalExpenses={this.state.totalExpenses} />
                <BuildControl 
                clicked={(data) => this.addSummaryHandler(data)} />
                <Button clicked={this.showSummaryHandler}>Show Summary</Button>
            </Aux>
        );
    }
}

export default BudgetBuilder;
