import * as actionTypes from '../actions/actions';
import { updateObject } from '../../Utility/Utility';

const initialState = {
        inc: {
            description: null,
            value: null,
            type: 'inc'
        },
        exp: {
            description: null,
            value: null,
            type: 'exp'
        }
    }

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.TAKE_INPUT:
            const updatedIncExp = updateObject(state[action.payLoad.incExp][action.payLoad.type], {
                [action.payLoad.type]: action.payLoad.event.target.value
            })
            return updateObject(state, {
                [action.payLoad.incExp]: updatedIncExp
            });
        case actionTypes.ADD_iTEM:
            return {};
        case actionTypes.REMOVE_iTEM:
            return {};
        default:
            return state;
    }
}

export default reducer;
