import React, { Component } from 'react';
import { connect } from 'react-redux';
import classes from './BuildControl.module.css';
import Input from '../../../components/UI/Input/Input';
import Card from '../../../components/UI/Card/Card';
import Button from '../../../components/UI/Button/Button';
import * as actionTypes from '../../../store/actions/actions';

class BuildControl extends Component {

    render () {
        return (
            <div className={classes.BuildControl}>
                <div className={classes.Inc}>
                    <Card>
                        <h2>INCOMES</h2>
                        <Input inputType='description' 
                            value={this.props.inc.description} 
                            changed={event => this.props.onChangeHandler(event, 'inc', 'description')} />
                        <Input inputType='value'
                            value={this.props.inc.value}
                            changed={event => this.props.onChangeHandler(event, 'inc', 'value')} />
                        <Button clicked={() => this.props.clicked(this.props.inc)}>add</Button>
                    </Card>
                </div>
                <div className={classes.Exp}>
                <Card>
                        <h2>EXPENSES</h2>
                        <Input inputType='description'
                            value={this.props.exp.description}
                            changed={event => this.props.onChangeHandler(event, 'exp', 'description')} />
                        <Input inputType='value'
                            value={this.props.exp.description}
                            changed={event => this.props.onChangeHandler(event, 'exp', 'value')} />
                        <Button clicked={() => this.props.clicked(this.props.exp)}>add</Button>
                    </Card>
                </div>
            </div>
        );
    }
} 

const matchStateToProps = state => {
    return {
        inc: state.inc,
        exp: state.exp
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onChangeHandler: (event, incExp, type) => dispatch({type: actionTypes.TAKE_INPUT,
                                                            payload: {
                                                                event: event,
                                                                incExp: incExp,
                                                                type: type
                                                            }})
    }
}


export default connect(matchStateToProps, mapDispatchToProps)(BuildControl);
