export const ADD_iTEM = 'ADD_iTEM';
export const REMOVE_iTEM = 'REMOVE_iTEM';
export const TAKE_INPUT = 'TAKE_INPUT';
export const SHOW_SUMMARY = 'SHOW_SUMMARY';
export const  HIDE_SUMMARY = 'HIDE_SUMMARY';

export const takeInput = (event, incExp, type) => {
    return {
        type: TAKE_INPUT,
        payLoad: {
            event: event,
            incExp: incExp,
            type: type
        }
    }
};

export const addItem = (data) => {
    return {
        type: ADD_iTEM,
        data: data,
    }
};

export const removeItem = (type, id) => {
    return {
        type: REMOVE_iTEM,
        payLoad: {
            type: type,
            id: id
        }
    }
};

export const showSummary = () => {
    return {
        type: SHOW_SUMMARY
    }
};

export const hideSummary = () => {
    return {
        type: HIDE_SUMMARY
    }
};
