import React from 'react';
import BudgetSummary from '../BudgetSummary';
import classes from './ExpSummary.module.css';
import Card from '../../UI/Card/Card';
import Icon from '../../UI/Icon/Icon';

const expSummary = props => {

    const summary = props.expensesSummary.map(exp => {
        return (
            <li key={exp.id}>
                <div className={classes.ExpSummary} >
                    <div className={classes.Exp}><h3>{exp.des}</h3></div>
                    <div className={classes.Exp}>
                        <h3>{exp.val}</h3>
                        <div className={classes.Card}>
                            <Card>
                                <p>{exp.perc}%</p>
                            </Card>
                        </div>
                    </div>
                    <div onClick={() => props.delete('exp', exp.id)}>
                        <Icon type='remove' />
                    </div>
                </div>
            </li>
        );
    })

    return (
        <BudgetSummary>
            <h1>Expenses</h1>
            <ul>
                {summary}
            </ul>
        </BudgetSummary>
    );
}

export default expSummary;
