import React, { Component } from 'react';
import classes from './BuildControl.module.css';
import Input from '../../../components/UI/Input/Input';
import Card from '../../../components/UI/Card/Card';
import Button from '../../../components/UI/Button/Button';

class BuildControl extends Component {

    state = {
        inc: {
            description: {
                value: null,
                rules: {
                    isRequired: true,
                    minLength: 3
                }
            },
            value: {
                value: null,
                rules: {
                    isRequired: true,
                    isNumeric: true
                }
            },
            type: 'inc'
        },
        exp: {
            description: {

            },
            value: {

            },
            type: 'exp'
        }
    }


    onChangeHandler = (event, incExp, type) => {
        const updatedState = {
            ...this.state
        };
        updatedState[incExp][type] = event.target.value;
        this.setState({state: updatedState});
    }


    render () {
        return (
            <div className={classes.BuildControl}>
                <div className={classes.Inc}>
                    <Card>
                        <h2>INCOMES</h2>
                        <Input inputType='description' 
                            value={this.state.inc.description} 
                            changed={event => this.onChangeHandler(event, 'inc', 'description')} />
                        <Input inputType='value'
                            value={this.state.inc.value}
                            changed={event => this.onChangeHandler(event, 'inc', 'value')} />
                        <Button clicked={() => this.props.clicked(this.state.inc)}>add</Button>
                    </Card>
                </div>
            <div className={classes.Exp}>
                <Card>
                    <h2>EXPENSES</h2>
                    <Input inputType='description'
                        value={this.state.exp.description}
                        changed={event => this.onChangeHandler(event, 'exp', 'description')} />
                    <Input inputType='value'
                        value={this.state.exp.description}
                        changed={event => this.onChangeHandler(event, 'exp', 'value')} />
                    <Button clicked={() => this.props.clicked(this.state.exp)}>add</Button>
                </Card>
            </div>
        </div>
    );
}
} 
export default BuildControl;
