import * as actionTypes from '../actions/actions';

const initialState = {
        inc: {
            description: null,
            value: null,
            type: 'inc'
        },
        exp: {
            description: null,
            value: null,
            type: 'exp'
        }
    }

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.TAKE_INPUT:
            const updatedState = {
            ...state
        };
        updatedState[action.incExp][action.type] = action.event.target.value;
        this.setState({state: updatedState});
            return updatedState;
        case actionTypes.ADD_iTEM:
            return {};
        case actionTypes.REMOVE_iTEM:
            return {};
        default:
            return state;
    }
}

export default reducer;
