import React, { Component } from 'react';
import { connect } from 'react-redux';
import classes from './BuildControl.module.css';
import Input from '../../../components/UI/Input/Input';
import Card from '../../../components/UI/Card/Card';
import Button from '../../../components/UI/Button/Button';
import { takeInput, addItem } from '../../../store/actions/budgetBuilder';

class BuildControl extends Component {

    render () {
        return (
            <div className={classes.BuildControl}>
                <div className={classes.Inc}>
                    <Card>
                        <form>
                            <h2>INCOMES</h2>
                            <Input inputType='description' 
                                value={this.props.inc.description}
                                changed={(event) => this.props.onChange(event, 'inc', 'description')} />
                            <Input inputType='value'
                                value={this.props.inc.value}
                                changed={(event) => this.props.onChange(event, 'inc', 'value')} />
                            <Button onSubmit={(event) => this.props.onAddItem(this.props.inc, event)}>add</Button>
                        </form>
                    </Card>
                </div>
                <div className={classes.Exp}>
                    <Card>
                        <form>
                            <h2>EXPENSES</h2>
                            <Input inputType='description'
                                value={this.props.exp.description}
                                changed={(event) => this.props.onChange(event, 'exp',  'description')} />
                            <Input inputType='value'
                                value={this.props.exp.value}
                                changed={(event) => this.props.onChange(event, 'exp', 'value')} />
                            <Button onSubmit={(event) => this.props.onAddItem(this.props.exp, event)} >add</Button>
                        </form>    
                    </Card>
                </div>
            </div>
        );
    }
} 

const matchStateToProps = state => {
    return {
        inc: state.budgetBuilder.data.inc,
        exp: state.budgetBuilder.data.exp
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onChange: (event, incExp, type) => dispatch(takeInput(event, incExp, type)),
        onAddItem: (data, event) => dispatch(addItem(data, event))
    }
}


export default connect(matchStateToProps, mapDispatchToProps)(BuildControl);
