import * as actionTypes from '../actions/actions';
import { updateObject } from '../../Utility/Utility';

const initialState = {
        budgetSummary: {
            inc: [],
            exp: []
        },
        totalBudget: 0,
        totalIncome: 0,
        totalExpenses: 0,
        totalPercentage: null,
        data: {
            inc: {
                description: null,
                value: null,
                type: 'inc'
            },
            exp: {
                description: null,
                value: null,
                type: 'exp'
            }
        }
    }

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.TAKE_INPUT:
            const updatedIncExp = updateObject(state.data[action.payLoad.incExp], {
                [action.payLoad.type]: action.payLoad.event.target.value
            });
            const updatedData = updateObject(state.data, {
                [action.payLoad.incExp]: updatedIncExp
            });
            return updateObject(state, {
                data: updatedData
            });

        case actionTypes.ADD_iTEM:
            return {};
        case actionTypes.REMOVE_iTEM:
            return {};
        default:
            return state;
    }
}

export default reducer;
