import React, { Component } from 'react';
import Aux from '../../hoc/Aux/Aux';
import Budget from '../../components/budget/Budget';
import BuildControl from './BuildControl/BuildControl';
import { updateObject } from '../../Utility/Utility';
import Modal from '../../components/UI/Modal/Modal';
import IncSummary from '../../components/BudgetSummary/IncSummary/IncSummary';


class BudgetBuilder extends Component {

    state = {
        budgetSummary: {
            inc: [],
            exp: []
        },
        totalBudget: 0,
        totalIncome: 0,
        totalExpenses: 0,
        clearFields: false,
    }

    addSummaryHandler = data => {
        const summary = {des: data.description, val: data.value};

        const updatedIncExp = [ 
            ...this.state.budgetSummary[data.type] 
            ];

        updatedIncExp.push(summary);

        const updatedBudgetSummary = updateObject(this.state.budgetSummary,{
            [data.type]: updatedIncExp
        });

        let budget = this.state.totalBudget;
        let income = this.state.totalIncome;
        let expense = this.state.totalExpenses;

        if (data.type === 'inc') {
            budget = budget + parseFloat(data.value);
            income = income + parseFloat(data.value);
        }

        if (data.type === 'exp') {
            budget = budget - parseFloat(data.value);
            expense = expense + parseFloat(data.value);
        }

        this.setState({
            budgetSummary: updatedBudgetSummary,
            totalBudget: budget,
            totalIncome: income,
            totalExpenses: expense,
            clearFields: true
        });

    }

    componentDidUpdate() {
        console.log(this.state);
    }

    render () {
        return (
            <Aux>
                <Budget
                totalBudget={this.state.totalBudget}
                totalIncome={this.state.totalIncome}
                totalExpenses={this.state.totalExpenses} />
                <BuildControl 
                clicked={(data) => this.addSummaryHandler(data)}
                clearFields={this.state.clearFields} />
            </Aux>
        );
    }
}

export default BudgetBuilder;
