import React, { Component } from 'react';
import { connect } from 'react-redux';
import Aux from '../../hoc/Aux/Aux';
import Budget from '../../components/budget/Budget';
import BuildControl from './BuildControl/BuildControl';
import Modal from '../../components/UI/Modal/Modal';
import ExpSummary from '../../components/BudgetSummary/ExpSummary/ExpSummary';
import Button from '../../components/UI/Button/Button';
import classes from './BudgetBuilder.module.css';
import { addItem, removeItem } from '../../store/actions/actions';


class BudgetBuilder extends Component {

    state = {
        showSummary: false
    }

    showSummaryHandler = () => {
        this.setState({showSummary: true});
    }

    hideSummaryHandler = () => {
        this.setState({showSummary: false});
    }

    componentDidUpdate() {
        console.log(this.props.budgetSummary);
    }

    render () {
        return (
            <Aux>
                <Modal show={this.state.showSummary} modalClosed={this.hideSummaryHandler}>
                    <ExpSummary 
                        expensesSummary={this.props.budgetSummary.exp} 
                        delete={this.props.onRemoveItem} />
                </Modal> 
                <Budget
                totalBudget={this.props.totalBudget}
                totalIncome={this.props.totalIncome}
                totalExpenses={this.props.totalExpenses}
                totalPercentage={this.props.totalPercentage} />
                <BuildControl 
                clicked={(data) => this.props.onAddedItem(data)} />
                <div className={classes.Btn}>
                    <Button clicked={this.showSummaryHandler}>Show Summary</Button>
                </div>
            </Aux>
        );
    }
}

const mapStateToProps = state => {
    return {
        budgetSummary: state.budgetSummary,
        totalBudget: state.totalBudget,
        totalIncome: state.totalIncome,
        totalExpenses: state.totalExpenses,
        totalPercentage: state.totalPercentage,
        data: state.data,
    }
};

const mapDispatchToProps = dispatch => {
    return {
        onAddedItem: (data) => dispatch(addItem(data)),
        onRemoveItem: (type, id) => dispatch(removeItem(type, id))
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(BudgetBuilder);
