import React from 'react';
import classes from './Modal.module.css';

const modal = props => {
    return (
        <div className={classes.Modal}
            style={{
                transform: props.show ? 'translateX(0)' : 'translateX(-100vw)'
            }} >
            {props.children}
        </div>
    );
}

export default modal;
